import React, {useEffect, useState} from 'react';  
import { Footer, Header } from '../components/index';
import { OurServicesSection, PricePlan, StudentJumbotron, BlogSection, BenefitsSection, Reviews } from '../components/home';
import gpl from 'graphql-tag';
import { Link } from 'react-router-dom';
import {
        Switch, Icon, Checkbox } from 'antd';
import {
  Card,
  CardBody,
  NavItem,
  NavLink,
  Nav,
  TabContent,
  TabPane,
  Row,
  Col,
    FormGroup,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
    Container
} from "reactstrap";
import classnames from "classnames";
import Classroom from "./ClassroomPage";
import { graphql } from 'react-apollo';
import { getPlans } from '../actions/plans';
import { connect } from 'react-redux';
import 'slick-carousel/slick/slick-theme.css';
import 'slick-carousel/slick/slick.css';
import '../assets/css/studentpage.css';
import {useDropzone} from 'react-dropzone';
import ClassroomPage from './ClassroomPage';
const thumbsContainer = {
  display: 'flex',
  flexDirection: 'row',
  flexWrap: 'wrap',
  marginTop: 16
};

const thumb = {
  display: 'inline-flex',
  borderRadius: 2,
  border: '1px solid #eaeaea',
  marginBottom: 8,
  marginRight: 8,
  width: 100,
  height: 100,
  padding: 4,
  boxSizing: 'border-box'
};

const thumbInner = {
  display: 'flex',
  minWidth: 0,
  overflow: 'hidden'
};

const img = {
  display: 'block',
  width: 'auto',
  height: '100%'
};


function Previews(props) {
  const [files, setFiles] = useState([]);
  const {getRootProps, getInputProps} = useDropzone({
    accept: 'image/*',
    onDrop: acceptedFiles => {
      setFiles(acceptedFiles.map(file => Object.assign(file, {
        preview: URL.createObjectURL(file)
      })));
    }
  });
  
  const thumbs = files.map(file => (
    <div style={thumb} key={file.name}>
      <div style={thumbInner}>
        <img
          src={file.preview}
          style={img}
        />
      </div>
    </div>
  ));

  useEffect(() => () => {
    // Make sure to revoke the data uris to avoid memory leaks
    files.forEach(file => URL.revokeObjectURL(file.preview));
  }, [files]);

  return (
    <section className="container" >
      <div {...getRootProps({className: 'dropzone py-5 text-center cursor-pointer'})}>
        <input {...getInputProps()} />
        <p>Upload Image....</p>
      </div>
      <aside style={thumbsContainer}>
        {thumbs}
      </aside>
    </section>
  );
}
class StudentPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      page: props.page,
    iconTabs: 2,
    plainTabs: 2,
    subiconTabs: 1,
    subplainTabs: 1,
        mobile:false,
        filter:true,
        width: 0, height: 0,
  };

  this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
}

componentDidMount() {
  this.updateWindowDimensions();
  window.addEventListener('resize', this.updateWindowDimensions);
}

componentWillUnmount() {
  window.removeEventListener('resize', this.updateWindowDimensions);
}

updateWindowDimensions() {
    if(window.innerWidth <= 544 ){
        this.setState({ iconTabs:2,mobile:true,
plainTabs: 2});
    }else{
         this.setState({ iconTabs:2,mobile:false,
plainTabs: 2});
    }
  this.setState({ width: window.innerWidth, height: window.innerHeight });
}
  toggleNavs = (e, state, index) => {
    e.preventDefault();
    this.setState({
      [state]: index
    });
  };
subtoggleNavs = (e, state, index) => {
    e.preventDefault();
    this.setState({
      [state]: index
    });
  };

showfilter(){
    this.setState({
        filter:true
    })

}
hidefilter(){
    this.setState({
        filter:false
    })

}


  render() {
    const { page } = this.state;

    return (
      <div>
        <Header parent="home" page={page} />
        <main ref="main" >
        <section>
            <Container fluid className=""> 
                <Row>
       
        <Col className="mt-1 ml-auto mr-auto col-12" lg="5">
            {/* Menu */}

            <div className="nav-wrapper">
              <Nav
                className="nav-fill flex-column flex-md-row"
                id="tabs-icons-text"
                pills
                role="tablist"
              >
                <NavItem>
                  <NavLink
                    aria-selected={this.state.plainTabs === 1}
                    className={classnames("mb-sm-3 mb-md-0 my-family-bold f-15 ", {
                      active: this.state.plainTabs === 1
                    })}
                    onClick={e => this.toggleNavs(e, "plainTabs", 1)}
                    href="#pablo"
                    role="tab"
                  >
                    BLOGS
                  </NavLink>
                </NavItem>
                <NavItem>
                  <NavLink
                    aria-selected={this.state.plainTabs === 2}
                    className={classnames("mb-sm-3 f-15 my-family-bold mb-md-0", {
                      active: this.state.plainTabs === 2
                    })}
                    onClick={e => this.toggleNavs(e, "plainTabs", 2)}
                    href="#pablo"
                    role="tab"
                  >
                    JOBS
                  </NavLink>
                </NavItem>
                <NavItem>
                  <Link to="/classroompage"
                    aria-selected={this.state.plainTabs === 4}
                    className={classnames("nav-link", "mb-sm-3 f-15 my-family-bold mb-md-0", {
                      active: this.state.plainTabs === 4
                    })}
                    // onClick={e => this.toggleNavs(e, "plainTabs", 4)}
                  //    onClick={(e) => {
                  //     alert('Its alive!')
                  // }}
                     //onClick={}
                  >
                    ZT Classroom
                  </Link>
                </NavItem>
                <NavItem>
                  <NavLink
                    aria-selected={this.state.plainTabs === 3}
                    className={classnames("mb-sm-3 f-15 my-family-bold mb-md-0", {
                      active: this.state.plainTabs === 3
                    })}
                    onClick={e => this.toggleNavs(e, "plainTabs", 3)}
                    href="#pablo"
                    role="tab"
                  >
                    EVENTS
                  </NavLink>
                </NavItem>
              </Nav>
            </div>
            </Col>
        
             <Col className="mt-1" lg="12">
            <Card className="border-0">
              <CardBody>
                <TabContent activeTab={"plainTabs" + this.state.plainTabs}>
                  <TabPane tabId="plainTabs1">
                    <p className="description">
                      Raw denim you probably haven't heard of them jean shorts
                      Austin. Nesciunt tofu stumptown aliqua, retro synth master
                      cleanse. Mustache cliche tempor, williamsburg carles vegan
                      helvetica. Reprehenderit butcher retro keffiyeh
                      dreamcatcher synth.
                    </p>
                    <p className="description">
                      Raw denim you probably haven't heard of them jean shorts
                      Austin. Nesciunt tofu stumptown aliqua, retro synth master
                      cleanse.
                    </p>
                  </TabPane>
                  <TabPane tabId="plainTabs2">
                       <Row>
                        <Col md={3} className="order-1-sm text-center mb-5">
                            <Card className="shadow pt-4 pb-4 border-0">
                                <div>
                                    <div className="col-12">
                                        <img
                                          alt="..."
                                          className="rounded-circle img-center border-zoo img-fluid shadow shadow-lg--hover hei-100"
                                          src={require("../assets/img/theme/team-1-800x800.jpg")}
                                        />
                                    </div>
                                    <div className="col-12">
                                        <p className="f-17 my-family text-black font-weight-500 text-center mt-3">Eugen Bardill</p>
                                        <p className="f-12 text-mutedd my-family  text-center mt-2"><i className="fa fa-map-marker"></i> San Francisco</p>
                                    </div>
                                        <div className="col-12">    
                                           <Row className="border-top border-bottom">
                                               <div className="col-6 border-right">
                                         <p className="mb-0 mt-3 my-family-cond-bold f-14 text-black">56</p>
                                         <p className="f-12 my-family text-mutedd">Recommendations</p>
                                    </div>
                                    <div className="col-6 border-left">
                                          <p className="mb-0 mt-3 my-family-cond-bold f-14 text-black">7</p>
                                           <p className="f-12 my-family text-mutedd">Views</p>
                                    </div>
                                               </Row>
                                    </div>
                                    <div className="col-12 pt-3">
                                         <Link  to="/studentprofile" className="  primary-color-text f-14 my-family-medium font-weight-500">View Profile </Link>
                                    </div>
                                </div>
                             
                                   
                                   
                                  
                                   </Card>
                        </Col>
                                   
                        <Col md={6} className="order-3-sm">
                          <Row>
                              <Col md={12} className="mr-auto">
                                    <div className="nav-wrapper">
                                          <Nav
                                            className="row nav-fill flex-column flex-md-row"
                                            id="tabs-icons-text"
                                            pills
                                            role="tablist"
                                          >
                                            <NavItem className="col-md-4">
                                              <NavLink
                                                aria-selected={this.state.subplainTabs === 1}
                                                className={classnames("mb-sm-3 mb-md-0 my-family-bold f-14 p-0", {
                                                  active: this.state.subplainTabs === 1
                                                })}
                                                onClick={e => this.subtoggleNavs(e, "subplainTabs", 1)}
                                                href="#pablo"
                                                role="tab"
                                              >
                                                    <img src={require('../assets/share.png')} />
                                                <span className="my-family text-black f-14 ml-2">Share and Update</span>
                                              </NavLink>
                                            </NavItem>
                                            <NavItem className="col-md-4">
                                              <NavLink
                                                aria-selected={this.state.subplainTabs === 2}
                                                className={classnames("mb-sm-3 f-14 my-family-bold mb-md-0 p-0", {
                                                  active: this.state.subplainTabs === 2
                                                })}
                                                onClick={e => this.subtoggleNavs(e, "subplainTabs", 2)}
                                                href="#pablo"
                                                role="tab"
                                              >
                                                <img src={require('../assets/upload.png')} />
                                                <span className="my-family text-black f-14 ml-2">Upload Photo</span>
                                              </NavLink>
                                                </NavItem>
                                            <NavItem className="col-md-4">
                                              <NavLink
                                                aria-selected={this.state.subplainTabs === 3}
                                                className={classnames("mb-sm-3 f-14 my-family-bold mb-md-0 p-0", {
                                                  active: this.state.subplainTabs === 3
